import React, { Component } from 'react';
import './Clock.css';

class Clock extends Component {
  constructor() {
    super();
    const time = new Date();

    this.state = {
      hours: time.getHours(),
      min: time.getMinutes(),
      second: time.getSeconds(),
      currentTime: this.hours + ':' + this.min + ':' + this.second,
    };
  }

  myTime() {
    if (this.state.hours > 12) {
      this.setState({ hours: this.state.hours - 12 });
    } else if (this.state.hours == 0) {
      this.setState({ hours: '0' + this.state.hours });
    }
  }

